import React from 'react';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSearchLocation} from "@fortawesome/free-solid-svg-icons";
import {setActiveContent, showOverlayContent} from "data/redux/action/content";
import {buildContentPath, buildQuery} from "data/util";
import {useHistory} from "react-router";
import {useDispatch, useSelector} from "react-redux";

const SearchCurrentButton = () => {

    const dispatch = useDispatch();
    const history = useHistory();
    const {activeChannel} = useSelector(state => state.channel);
    const {contentLoading} = useSelector(state => state.content);
    const {mapBounds} = useSelector(state => state.map);

    const searchCurrentLocation = () => {
        dispatch(setActiveContent(null));
        dispatch(showOverlayContent(null));
        history.push({
            pathname: buildContentPath(activeChannel, null),
            search: buildQuery({page: 1, swLat: mapBounds.sw.lat, swLng: mapBounds.sw.lng, neLat: mapBounds.ne.lat, neLng: mapBounds.ne.lng})
        });
    };
    return (
        <button className={"location-search-button button is-link has-text-weight-semibold " + (contentLoading? "is-loading" : "")} onClick={searchCurrentLocation}>
            <span className="icon">
                <FontAwesomeIcon icon={faSearchLocation} size={"sm"} />
            </span>
            <span>현재 지도에서 찾기</span>
        </button>
    )
};

export default SearchCurrentButton;
