import React from 'react';
import axios from 'axios';


const InputField = (props) => <>
    <div className="field is-horizontal">
        <div className="field-label is-normal">
            <label className="label">{props.fieldName}</label>
        </div>
        <div className="field-body">
            <div className="field">
                <p className="control is-expanded">
                    <input className="input" name={props.name} type="text" placeholder={props.placeholder} value={props.value} onChange={props.onChange} />
                </p>
            </div>
        </div>
    </div>
</>

const SelectBox = (props) => <>
    <div className="field is-horizontal">
        <div className="field-label is-normal">
            <label className="label">{props.fieldName}</label>
        </div>
        <div>
            <Select
                name="form-field-name"
                value={this.state.value}
                onChange={(ev => this.handleChangeInput(ev))}
                labelKey={'name'}
                valueKey={'code'}
                options={this.state.channels}
            />
        </div>
    </div>
</>
class MetaRegistForm extends React.Component {
    componentDidMount() {
        debugger;
        axios.get("http://localhost/api/channels")
            .then(res => {
                const channels = res.data.youtubeName;
                this.setState({ channels: channels });
            })
    }

    constructor(props) {
        super(props);
        this.state = {
            channels: [],
            youtubeName: '',
            youtubeContentsLink: '',
            contentsTitle: '',
            tag: '',
            store: '',
            tel: '',
            address1: '',
            address2: '',
            storeLink: '',
            latitude: '',
            longitude: '',
            availableTime: ''
            // menu: 'menu'
        };
    }

    handleChangeInput = (ev) => {
        const inputName = ev.target.name;
        this.setState({[inputName]: ev.target.value});
    }

    handleClick = (ev) => {
        const storeName = this.state.store;
        axios.get("http://localhost:8080/admin/getStore?store=" + storeName)
            .then(response => {
                const data = response.data;
                this.setState({
                    tel: data.tel,
                    address1: data.address1,
                    address2: data.address2,
                    storeLink: data.link,
                    latitude: data.latitude,
                    longitude: data.longitude,
                    availableTime: data.availableTime
                });
            });
        ev.stopPropagation();

    }

    handleSubmit = (ev) => {
        ev.preventDefault();
        const data = new FormData(ev.target.form);
        axios.post("http://localhost:8080/admin/contentsForm", data)
            .then(response => {
                if (response.data === "success") {
                    alert("정상적으로 등록되었습니다.")
                    this.setState({
                        youtubeName: '',
                        youtubeContentsLink: '',
                        contentsTitle: '',
                        registerDate: '',
                        tag: '',
                        store: '',
                        tel: '',
                        address1: '',
                        address2: '',
                        storeLink: '',
                        latitude: '',
                        longitude: '',
                        availableTime: ''
                    })
                } else {
                    alert("등록에 실패하였습니다.")
                }
            });
    }

    render() {
        let options = this.state.channels.map(function (city) {
            return city.name;
        })
        return (
            <form className="columns is-fullheight-with-navbar">
                <div>
                    <Select
                        name="form-field-name"
                        value={this.state.value}
                        onChange={this.handleChange}
                        clearable={this.state.clearable}
                        searchable={this.state.searchable}
                        options={options}
                    />
                </div>
                    <div className="column">
                        <InputField fieldName="유투버이름" name="youtubeName" placeholder="유투버이름" value={this.state.youtubeName} onChange={(ev => this.handleChangeInput(ev))}/>
                        <InputField fieldName="영상링크" name="youtubeContentsLink" placeholder="영상링크" value={this.state.youtubeContentsLink} onChange={(ev => this.handleChangeInput(ev))}/>
                        <InputField fieldName="컨텐츠제목" name="contentsTitle" placeholder="컨텐츠제목" value={this.state.contentsTitle} onChange={(ev => this.handleChangeInput(ev))}/>
                        <InputField fieldName="컨텐츠등록날짜" name="registerDate" placeholder="등록날짜" value={this.state.registerDate} onChange={(ev => this.handleChangeInput(ev))}/>
                        <InputField fieldName="태그" name="tag" placeholder="태그" value={this.state.tag} onChange={(ev => this.handleChangeInput(ev))}/>
                        <InputField fieldName="식당정보" name="store" placeholder="식당정보" value={this.state.store} onChange={(ev => this.handleChangeInput(ev))}/>
                        <button className="button is-primary is-pulled-right" type="button" onClick={this.handleClick}>주소 확인</button>
                    </div>
                    <div className="column">
                        <InputField fieldName="전화번호" name="tel" placeholder="전화번호" value={this.state.tel} onChange={(ev => this.handleChangeInput(ev))}/>
                        <InputField fieldName="주소1" name="address1" placeholder="주소1" value={this.state.address1} onChange={(ev => this.handleChangeInput(ev))}/>
                        <InputField fieldName="주소2" name="address2" placeholder="주소2" value={this.state.address2} onChange={(ev => this.handleChangeInput(ev))}/>
                        <InputField fieldName="링크" name="link" placeholder="링크" value={this.state.storeLink} onChange={(ev => this.handleChangeInput(ev))}/>
                        <InputField fieldName="위도" name="latitude" placeholder="위도" value={this.state.latitude} onChange={(ev => this.handleChangeInput(ev))}/>
                        <InputField fieldName="경도" name="longitude" placeholder="경도" value={this.state.longitude} onChange={(ev => this.handleChangeInput(ev))}/>
                        <InputField fieldName="영업 시간" name="availableTime" placeholder="영업 시간" value={this.state.availableTime} onChange={(ev => this.handleChangeInput(ev))}/>
                        <button className="button is-primary is-pulled-right" type="submit" onClick={this.handleSubmit}>저장</button>
                    </div>
            </form>

        )
    }
}

export default MetaRegistForm
