import React, {useCallback} from 'react';
import MoobeKakaoMap from "components/common/map/MoobeKakaoMap";
import MarkerClusterer from "components/common/map/MarkerClusterer";
import {useDispatch, useSelector} from "react-redux";
import {showOverlayContentWithDiff} from "data/redux/action/content";
import InfoWindoWithMarker from "components/common/map/InfoWindowWithMarker";
import MarkerImage from 'assets/images/marker_gra_42x50.png';
import CustomOverlay from "components/common/map/CustomOverlay";
import MobileOverlay from "components/mobile/map/MobileOverlay";
import {isNil as _isNil} from 'lodash'
import Marker from "components/common/map/Marker";

const MobileKakaoMap = () => {
    const contentList = useSelector(state => state.content.contentList);
    return (
        <>
            <MoobeKakaoMap className="column is-paddingless kakao-map is-fullheight-with-navbar-mobile is-marginless">
                <MarkerClusterer options={{averageCenter: true, minLevel: 6}}>
                    {contentList.map(content => <MarkerWithCustomOverlay key={content.id} content={content} />)}
                </MarkerClusterer>
            </MoobeKakaoMap>
        </>
    )
};

const MarkerWithCustomOverlay = ({content}) => {
    const {activeOverlayContent} = useSelector(state => state.content);
    let visible = (!_isNil(activeOverlayContent) && content.id === activeOverlayContent.id);
    const dispatch = useDispatch();
    const updateOverlayContent = (content) => {
        dispatch(showOverlayContentWithDiff(content));
    };

    return (
        <>
            <Marker onClick={(marker) => updateOverlayContent(content)}
                options={{lat: content.store.latitude, lng: content.store.longitude, content: content.store.name,
                    image: {url: MarkerImage, width: 34, height: 42}}} />
            <CustomOverlay lat={content.store.latitude} lng={content.store.longitude} visible={visible} options={{offsetY: 54}}>
                <MobileOverlay content={content} />
            </CustomOverlay>
        </>
    )
};


export default MobileKakaoMap;
