import axios from 'axios';

export const CHANNEL_ADD_CHANNEL = 'CHANNEL/ADD_CHANNEL';
export const CHANNEL_SET_ACTIVE_CHANNEL = 'CHANNEL/SET_ACTIVE_CHANNEL';
export const CHANNEL_FETCH_CHANNEL = 'CHANNEL/FETCH_CHANNEL';
export const CHANNEL_FETCH_CHANNEL_SUCCESS = 'CHANNEL/FETCH_CHANNEL_SUCCESS';
export const CHANNEL_FETCH_CHANNEL_ERROR = 'CHANNEL/FETCH_CHANNEL_ERROR';


export const fetchChannel = (activeChannelId) => {
    return (dispatch) => {
        dispatch(fetchChannelData());
        //TODO set channel list from api
        return axios.get(process.env.REACT_APP_API_URL + "/api/contents")
            .then(response => {
                setTimeout(() => {
                    dispatch(fetchSuccessChannelData({data: response.data, activeChannelId: activeChannelId}))
                }, 1000)
            }).catch(function (error) {
                dispatch(fetchErrorChannelData(error))
            });
    }
};

export const fetchChannelData = () => {
    return {
        type: CHANNEL_FETCH_CHANNEL
    }
};

export const fetchSuccessChannelData = (data) => {
    return {
        type: CHANNEL_FETCH_CHANNEL_SUCCESS,
        data
    }
};

export const fetchErrorChannelData = (err) => {
    return {
        type: CHANNEL_FETCH_CHANNEL_ERROR,
        err
    }
};

export const setActiveChannel = (channelId) => {
    return {
        type: CHANNEL_SET_ACTIVE_CHANNEL,
        channelId
    }
};
