import {
    COMMENT_FETCH_MOOBE, COMMENT_FETCH_MOOBE_ERROR, COMMENT_FETCH_MOOBE_SUCCESS,
    COMMENT_FETCH_YOUTUBE, COMMENT_FETCH_YOUTUBE_ERROR,
    COMMENT_FETCH_YOUTUBE_SUCCESS,
    COMMENT_RESET_COMMENT
} from "../action/comment";
import {updateObject} from "./util";
import {exampleData} from "../model/Comment";
import Comment from "../model/Comment";

const initialStateComment = {
    videoId: null,
    moobeCommentList: [],
    moobeCommentPage: 0,
    moobeCommentMaxPage: 5,
    moobeCommentLoading: false,
    youtubeCommentList: [],
    youtubeCommentPage: 0,
    youtubeCommentMaxPage: 5,
    youtubeCommentLoading: false
};

const fetchSuccessYoutubeComment = (commentState, response) => {
    const {data, page} = response;
    //TODO change result from api
    let results = exampleData;
    let commentList = results.map(result => new Comment(result))
    return updateObject(commentState, {
        youtubeCommentLoading: false,
        youtubeCommentList: commentState.youtubeCommentList.concat(commentList),
        youtubeCommentPage: page
    })
};

const fetchSuccessMoobeComment = (commentState, response) => {
    const {data, page} = response;
    //TODO change result from api
    let results = exampleData;
    let commentList = results.map(result => new Comment(result));
    return updateObject(commentState, {
        moobeCommentLoading: false,
        moobeCommentList: commentState.moobeCommentList.concat(commentList),
        moobeCommentPage: page
    })
};

export default function(commentState = initialStateComment, action) {
    switch (action.type) {
        case COMMENT_RESET_COMMENT: return updateObject(commentState, initialStateComment);
        case COMMENT_FETCH_YOUTUBE: return updateObject(commentState, {youtubeCommentLoading: true});
        case COMMENT_FETCH_MOOBE: return updateObject(commentState, {moobeCommentLoading: true});
        case COMMENT_FETCH_YOUTUBE_SUCCESS: return fetchSuccessYoutubeComment(commentState, action.data);
        case COMMENT_FETCH_MOOBE_SUCCESS: return fetchSuccessMoobeComment(commentState, action.data);
        case COMMENT_FETCH_YOUTUBE_ERROR: return updateObject(commentState, {youtubeCommentLoading: false});
        case COMMENT_FETCH_MOOBE_ERROR: return updateObject(commentState, {moobeCommentLoading: false});
        default : return commentState;
    }
}
