import {updateObject} from "data/util";
import {map as _map} from "lodash";
import {
    NOTICE_FETCH_NOTICE_SUCCESS,
    NOTICE_FETCH_TODAY_NOTICE_SUCCESS
} from "data/redux/action/notice";
import Notice from "data/redux/model/Notice";

const initialStateNotice = {
    noticeList: [],
    todayNoticeList: null
};

function fetchNoticeListSuccess(noticeState, response) {
    const {data} = response;
    let noticeList = _map(data, result => new Notice(result));
    return updateObject(noticeState, {noticeList: noticeList})
}

function fetchTodayNoticeSuccess(noticeState, response) {
    const {data} = response;
    let noticeList = _map(data, result => new Notice(result));
    return updateObject(noticeState, {todayNoticeList: noticeList})
}

export default function(noticeState = initialStateNotice, action) {
    switch(action.type) {
        case NOTICE_FETCH_NOTICE_SUCCESS : return fetchNoticeListSuccess(noticeState, action.data);
        case NOTICE_FETCH_TODAY_NOTICE_SUCCESS : return fetchTodayNoticeSuccess(noticeState, action.data);
        default : return noticeState;
    }
}
