import React, {useState} from 'react';
import {useDispatch, useSelector} from "react-redux";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faChevronLeft} from "@fortawesome/free-solid-svg-icons";
import YouTube from "react-youtube";
import CommentList from "components/common/comment/CommentList";
import withKakaoMap from "components/common/map/WithKakaoMap";
import StaticKakaoMap from "components/common/map/StaticKakaoMap";
import {buildContentPath} from "data/util";
import {useHistory} from "react-router";
import {Sticky, StickyContainer} from 'react-sticky';
import {deepCompareSetState} from "data/util";
import ScrollManager from "components/common/scroll/ScrollManager";


const Loading = () => (
    <div className="row">
        <div className="column overlay-navbar-mobile has-text-centered">
            <FontAwesomeIcon className="is-pulled-left" icon={faChevronLeft} size={'2x'}/>
            <span><strong></strong></span>
        </div>
    </div>
);

const StaticKakao = withKakaoMap(StaticKakaoMap);

const StoreDetail = ({content}) => {
    const [stickyInfo, setStickyInfo] = useState({
        isSticky: false,
        calculatedHeight: 0
    });
    const {activeChannel} = useSelector(state => state.channel);
    const dispatch = useDispatch();
    const history = useHistory();
    const resetActiveContent = () => {
        history.push({
            pathname: buildContentPath(activeChannel, null),
            search: location.search
        });
    };

    const openUrl = (e) => {
        window.open(content.store.link, "_blank");
    };
    return (
        <>
        {!content
            ? <Loading />
            :
                <div className="row" style={{ touchAction: 'auto' }}>
                    <div className="column overlay-navbar-mobile has-text-centered">
                        <FontAwesomeIcon className="is-pulled-left has-margin-right-15" icon={faChevronLeft} size={'2x'} onClick={resetActiveContent}/>
                        <span><strong>{content.title}</strong></span>
                    </div>
                    <div className="column overlay-content-mobile is-fullheight-with-navbar-mobile is-marginless is-paddingless is-full has-background-white">
                        <div className="column is-paddingless is-full">
                            <div className="card-content">
                                <p className="is-marginless has-text-centered">
                                    <span className="title is-size-5 is-family-sans-serif has-cursor-pointer" onClick={openUrl}>{content.store.name}</span>
                                </p>
                                <hr className="is-top-marginless has-background-grey-lighter"/>
                                <StaticKakao className="box is-paddingless is-full has-height-150"
                                             options={{lat: content.store.latitude, lng: content.store.longitude, level: 3, markerOptions: {lat: content.store.latitude, lng: content.store.longitude, text: "가게위치"}}}/>
                                <div className="content is-family-sans-serif">
                                    <p className="is-size-6 has-margin-bottom-5">{content.store.address1}</p>
                                    <p className="is-size-6 has-margin-bottom-10"><span className="tag is-info is-rounded is-normal has-text-weight-bold has-margin-right-5">지번</span>{content.store.address2}</p>
                                    <p className="is-size-7 has-margin-bottom-5 has-text-link">{content.store.tel}</p>
                                    <p className="is-size-7 has-margin-bottom-5">{content.store.availableTime}</p>
                                </div>
                            </div>
                        </div>
                        <hr className="divider has-background-grey-lighter is-top-marginless"/>
                        <StickyContainer>
                            <Sticky>
                                {({style, isSticky, distanceFromTop, calculatedHeight}) => {
                                    deepCompareSetState({isSticky, calculatedHeight}, setStickyInfo);
                                    let top = isSticky ? '0' : distanceFromTop;
                                    let zIndex = isSticky ? 10 : 0;
                                    return (
                                        <div style={{...style, top, zIndex}}>
                                            <YouTube
                                                videoId={content.videoLinkId}                  // defaults -> null
                                                id={content.videoLinkId}                       // defaults -> null
                                                className="youtube-container-mobile"                // defaults -> null
                                                containerClassName="column has-text-centered is-full is-marginless is-paddingless is-top-borderless"       // defaults -> ''
                                                opts={{playerVars: {playsinline: 1}}}
                                                // onReady={func}                    // defaults -> noop
                                                // onPlay={func}                     // defaults -> noop
                                                // onPause={func}                    // defaults -> noop
                                                // onEnd={func}                      // defaults -> noop
                                                // onError={func}                    // defaults -> noop
                                                // onStateChange={func}              // defaults -> noop
                                                // onPlaybackRateChange={func}       // defaults -> noop
                                                // onPlaybackQualityChange={func}    // defaults -> noop
                                            />
                                        </div>
                                    )
                                }}
                            </Sticky>
                            <CommentList stickyInfo={stickyInfo} />
                        </StickyContainer>
                    </div>

                </div>
        }
        </>
    )
};


export default StoreDetail;
