import React, {useEffect} from 'react';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSearchLocation} from "@fortawesome/free-solid-svg-icons";
import {
    buildContentPath,
    buildQuery,
    getSearchQueryParams,
    getSearchQueryText,
    isSimilarLocation,
    updateObject
} from "data/util";
import {useHistory, useLocation} from "react-router";
import {useDispatch, useSelector} from "react-redux";
import {isEmpty as _isEmpty} from "lodash";
import queryString from "query-string";
import {setSearchOnBoundChange} from "data/redux/action/map";
import {setFocusedLocation} from "data/redux/action/location";
import {uuid} from "bulma-extensions/bulma-carousel/src/js/utils";

const SearchCurrentButton = () => {

    const dispatch = useDispatch();
    const history = useHistory();
    const {activeChannel} = useSelector(state => state.channel);
    const {contentList, contentLoading} = useSelector(state => state.content);
    const {centerChanged, mapBounds, searchOnBoundChange, changedCenterLat, changedCenterLng} = useSelector(state => state.map);
    const {query, searchType} = queryString.parse(useLocation().search);

    const search = (e) => {
        if (e) e.target.blur();
        dispatch(setFocusedLocation(null))
        let queryParams = updateObject(getSearchQueryParams(query, searchType)
            ,{page: 1, swLat: mapBounds.sw.lat, swLng: mapBounds.sw.lng, neLat: mapBounds.ne.lat, neLng: mapBounds.ne.lng, k: uuid().substring(0, 5)});
        history.push({
            pathname: buildContentPath(activeChannel, null),
            search: buildQuery(queryParams)
        });
    };

    const searchCurrentLocationText = () => {
        if (!_isEmpty(query)) {
            return "현재 지도에서 [" + getSearchQueryText(query, searchType) + "] 다시 찾기";
        }
        return "현재 지도에서 찾기"
    }

    const isVisible = () => {
        if (centerChanged) {
            if (contentLoading) {
                return true
            }
            if (contentList.length == 0) {
                return false
            }
            return true
        } else {
            return false
        }
    }

    useEffect(() => {
        if (searchOnBoundChange && mapBounds) {
            search();
            dispatch(setSearchOnBoundChange(false))
        }
    }, [mapBounds])


    return (
        <button className={"location-search-button button has-text-weight-semibold" + (contentLoading? " is-loading" : "") + (isVisible() ? "" : " is-hidden")} onClick={search}>
            <span className="icon">
                <FontAwesomeIcon icon={faSearchLocation} size={"sm"} />
            </span>
            <span>{searchCurrentLocationText()}</span>
        </button>
    )
};

export default SearchCurrentButton;
