import React, {useEffect, useState} from 'react';
import MoobeBrand from 'assets/images/moobe_brand.png';
import MoobeLogo from 'assets/images/moobe_logo52.png';
import {faUserCircle} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {useSelector} from 'react-redux';
import {useHistory, useLocation, useParams} from "react-router";
import {buildChannelPath} from "../../../redux/reducers/util";
import {Link, NavLink} from "react-router-dom";

const emptyImage = "data:image/gif;base64,R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7";
const NavContent = ({channel}) => (
    <>
        <div className="media-left">
            <figure className="image">
                {channel != null
                ? <img className="image has-min-height-32 has-min-width-32 is-32x32 is-rounded" src={channel.channelImageUrl}/>
                :<img className="image has-min-height-32 is-rounded" src={emptyImage}/> }
            </figure>
        </div>
        <div className="media-content align-self-center">
            <p className="subtitle">
                {channel != null ? channel.youtubeName : "전체"}
            </p>
        </div>
    </>
);

const NavItem = ({channel}) => {
    const {channelLoading} = useSelector(state => state.channel);
    const history = useHistory();
    return (
        <>
            <a className="navbar-item media is-marginless flex-mobile"
               onClick={() => channelLoading ? null : history.push(buildChannelPath(channel))}>
                <NavContent channel={channel}/>
            </a>
        </>
    )
};

const NavItems = () => {
    const [channelDropdownActive, setChannelDropdownActive] = useState(false);
    const {channelList, activeChannel, channelLoading} = useSelector(state => state.channel);

    useEffect(() => {
        setChannelDropdownActive(false)
    }, [activeChannel, channelLoading]);

    const channelDropdownClassName = () => channelLoading
        ? "navbar-item item has-dropdown is-paddingless-mobile disabled"
        : channelDropdownActive
            ? "navbar-item item has-dropdown is-paddingless-mobile is-active"
            : "navbar-item item has-dropdown is-paddingless-mobile";

    const mobileChannelDropdownClassName = () => channelLoading
        ? "navbar-dropdown disabled is-hidden-touch"
        : channelDropdownActive
            ? "navbar-dropdown"
            : "navbar-dropdown is-hidden-touch";

    const toggleChannelDropdown = () => channelLoading ? "" : setChannelDropdownActive(!channelDropdownActive);

    return (
        <div className={channelDropdownClassName()}>
            <div className="navbar-link flex-mobile " onClick={toggleChannelDropdown}>
                <NavContent channel={activeChannel}/>
            </div>
            <div className={mobileChannelDropdownClassName()}>
                {activeChannel != null ? <NavItem channel={null}/> : null}
                {channelList.map(channel => {
                    if (activeChannel != null && channel.id === activeChannel.id) {
                        return null;
                    }
                    return <NavItem key={channel.id} channel={channel}/>
                })}
            </div>
        </div>
    )
};

const SearchBar = () => {
    const {activeChannel, channelLoading} = useSelector(state => state.channel);
    return (
        <>
            {activeChannel != null
                ? <p className="control">
                    <a className="button is-static is-medium">
                        <img className="image has-min-height-32 has-min-width-32 is-32x32 is-rounded" src={activeChannel.channelImageUrl}/>
                    </a>
                </p>
                : null}
            <p className="control has-icons-right">
                <input className="input is-medium" type="search" placeholder="음식, 태그, 지역..."/>
                <span className="icon is-small is-right">
                  <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24">
                      <path fill="#ddd" d="M23.822 20.88l-6.353-6.354c.93-1.465 1.467-3.2 1.467-5.059.001-5.219-4.247-9.467-9.468-9.467s-9.468 4.248-9.468 9.468c0 5.221 4.247 9.469 9.468 9.469 1.768 0 3.421-.487 4.839-1.333l6.396 6.396 3.119-3.12zm-20.294-11.412c0-3.273 2.665-5.938 5.939-5.938 3.275 0 5.94 2.664 5.94 5.938 0 3.275-2.665 5.939-5.94 5.939-3.274 0-5.939-2.664-5.939-5.939z"/>
                  </svg>
                </span>
            </p>
        </>
    )
}

const MobileNavHeader = () => {
    return (
        <div className="navbar-item is-hidden-tablet mobile-search is-paddingless is-marginless has-margin-left-auto field has-addons">
            <SearchBar />
        </div>
    )
};

const DesktopSearchBar = () => {
    return (
        <div className="navbar-item is-hidden-mobile has-max-width-250-tablet-only is-paddingless has-padding-right-15 is-marginless field has-addons">
            <SearchBar />
        </div>
    )
}


const MoobeNavBar = () => {
    const [burgerActive, setBurgerActive] = useState(false);
    const {channelId, contentId} = useParams();
    const history = useHistory();
    const location = useLocation();

    useEffect(() => {
        setBurgerActive(false);
    }, [channelId, contentId]); //update active channel data every location changed.

    const burgerClassName = () => burgerActive ? "navbar-burger burger is-marginless is-active" : "navbar-burger burger is-marginless ";
    const navbarMenuClassName = () => burgerActive ? "navbar-menu has-text-centered has-padding-left-50-mobile has-padding-left-15-tablet is-active" : "navbar-menu has-text-centered has-padding-left-50-mobile has-padding-left-15-tablet";

    const toLogin = () => {
        window.location = process.env.REACT_APP_API_URL + "/login";
    }
    return (
        <>
            <nav className="navbar is-light">
                <section className="container">
                    <div className="navbar-brand has-padding-left-15">
                        <a role="button" onClick={() => history.push("/")}>
                            <img className="is-hidden-mobile" src={MoobeBrand} alt="Moobe"/>
                            <img className="is-hidden-tablet" src={MoobeLogo} alt="Moobe"/>
                        </a>
                        <MobileNavHeader />
                        <a role="button" onClick={() => setBurgerActive(!burgerActive)}
                           className={burgerClassName()} aria-label="menu" aria-expanded="false"
                           data-target="navbarMenu">
                            <span aria-hidden="true"></span>
                            <span aria-hidden="true"></span>
                            <span aria-hidden="true"></span>
                        </a>
                    </div>

                    <div id="navbarMenu" className={navbarMenuClassName()}>
                        <div className="navbar-start">
                            <NavItems />
                        </div>
                        <div className="navbar-end">
                            <DesktopSearchBar />
                            <a className="navbar-item item flex-mobile">
                                <p className="subtitle">지도</p>
                            </a>
                            <a className="navbar-item item flex-mobile">
                                <p className="subtitle">내 페이지</p>
                            </a>
                            <a className="navbar-item item flex-mobile">
                                <p className="subtitle">커뮤니티</p>
                            </a>
                            <div className="navbar-item">
                                <div onClick={(e) => toLogin()} className="button">
                                    <FontAwesomeIcon icon={faUserCircle} size='lg'/>
                                    <span className="has-padding-left-10"> 로그인</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </nav>
        </>
    )
};

export default MoobeNavBar;
