import React, {useEffect, useState} from "react";

const CommentInput = () => {
    const [textAreaRow, setTextAreaRow] = useState(1);
    const [inputValue, setInputValue] = useState("");
    const [showButton, setShowButton] = useState(false);
    const [disableButton, setDisableButton] = useState(true);

    useEffect(() => {
        setDisableButton(inputValue === "");
        setTextAreaRow(inputValue.split("\n").length);
    }, [inputValue]);

    const cancelComment = () => {
        setInputValue("");
        setShowButton(false);
    };

    const submitComment = () => {
        //TODO call api
        console.log("submit comment ", inputValue);
    };

    const CommentActionButton = () => (
        <div className="field is-grouped is-pulled-right has-padding-top-10">
            <div className="control">
                <button className="button is-light" onClick={cancelComment}>취소</button>
            </div>
            <div className="control">
                <button className="button is-link" disabled={disableButton} onClick={submitComment}>댓글</button>
            </div>
        </div>
    );

    return (
        <div className="card is-shadowless">
            <div className="card-content is-paddingless has-padding-15">
                <div className="media">
                    <div className="media-left">
                        <figure className="image is-48x48">
                            <img className="is-rounded" src="https://bulma.io/images/placeholders/96x96.png" alt="Placeholder image" />
                        </figure>
                    </div>
                    <div className="media-content">
                        <textarea className="textarea has-fixed-size overflow-hidden is-top-borderless is-left-borderless is-right-borderless is-shadowless is-radiusless has-padding-5"
                                  value={inputValue}
                                  rows={textAreaRow}
                                  onFocus={(e) => setShowButton(true)}
                                  onChange={(e) => setInputValue(e.target.value)}  />
                        {showButton ? <CommentActionButton /> : null}
                    </div>
                </div>
            </div>
        </div>
    )
};

export default CommentInput;
