import React, {useEffect, useState} from 'react';
import MetaRegistForm from "components/admin/MetaRegistForm";
import {authorizeUser, requireUser} from "data/redux/action/user";
import {fetchChannel} from "data/redux/action/channel";
import {useDispatch} from "react-redux";
import {useHistory} from "react-router";
import NoticeForm from "components/admin/NoticeForm";


const AdminHome = () => {
    const [activeTab, setActiveTab] = useState(0);
    const dispatch = useDispatch();
    const history = useHistory();
    const tabContent = {
        0 : <MetaRegistForm />,
        1 : <NoticeForm />,
    }

    useEffect(() => {
        dispatch(authorizeUser(() => {
            dispatch(requireUser())
            dispatch(fetchChannel())
        },() => {
            console.log("failed to authorize.")
            history.replace("/")
        }));
    }, []);


    const getActiveTabClass = (index) => {
        return activeTab === index ? "is-active" : "";
    }

    return (
        <>
            <div className="row tabs is-boxed is-bottom-marginless white-space-normal"  style={{marginLeft: '-1px'}}>
                <ul className="column is-paddingless">
                    <li className={getActiveTabClass(0)}><a onClick={() => setActiveTab(0)}><strong>컨텐츠 등록</strong></a></li>
                    <li className={getActiveTabClass(1)}><a onClick={() => setActiveTab(1)}><strong>공지사항 등록</strong></a></li>
                </ul>
            </div>
            <div className="row box is-top-borderless is-radiusless">
                <div className="column">
                    {tabContent[activeTab]}
                </div>
            </div>
        </>
    )
}

export default AdminHome;
