import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faThumbsUp as fasTumbsUp} from "@fortawesome/free-solid-svg-icons";
import React from "react";

const Comment = ({comment}) => {
    return (
        <>
            {/*TODO comment가 길때 더보기 구현*/}
            <div className="card is-shadowless">
                <div className="card-content is-paddingless has-padding-15">
                    <div className="media">
                        <div className="media-left">
                            <figure className="image is-48x48">
                                <img className="is-rounded" src="https://bulma.io/images/placeholders/96x96.png" alt="Placeholder image" />
                            </figure>
                        </div>
                        <div className="media-content">
                            <div>
                                {comment.register}
                                <small className="has-padding-left-5 has-text-grey-light has-text-weight-semibold">{comment.registerDate}</small>
                            </div>
                            <div>{comment.comment}</div>
                            <div>
                                <FontAwesomeIcon icon={fasTumbsUp}
                                                 className="has-cursor-pointer"
                                                 color={comment.liked ? "#3ea6ff" : "#909090"}/> <small>{comment.likeCount}</small>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default React.memo(Comment);
