import {
    MAP_ADD_MARKER_WITH_CONTENT,
    MAP_LOADING_MAP, MAP_LOADING_MAP_ERROR, MAP_LOADING_MAP_SUCCESS,
    MAP_SET_MAP, MAP_SET_MAP_CENTER, MAP_SET_MAP_REF
} from '../action/map';

import { updateObject } from './util';

const initialStateMap = {
    map: null,
    mapRef: null,
    mapLoading: false,
    errorMapLoading: false,
    centerLat: null,
    centerLng: null,
};

export default function(mapState = initialStateMap, action) {
    switch(action.type) {
        case MAP_SET_MAP : return updateObject(mapState, {map: action.map});
        case MAP_SET_MAP_REF : return updateObject(mapState, {mapRef: action.mapRef});
        case MAP_SET_MAP_CENTER : return updateObject(mapState, {centerLat: action.latlng.lat, centerLng: action.latlng.lng});
        case MAP_LOADING_MAP : return updateObject(mapState, {mapLoading: true});
        case MAP_LOADING_MAP_SUCCESS : return updateObject(mapState, {map: action.map, mapRef: action.mapRef, kakao: action.kakao, mapLoading: false});
        case MAP_LOADING_MAP_ERROR : return updateObject(mapState, {map: null, errorMapLoading: true, mapLoading: false});
        default : return mapState;
    }
}
