import React from 'react';
import MarkerArrow from 'assets/images/marker_arrow.png';
import {useSelector} from "react-redux";
import {toString as _toString} from "lodash";
import {buildContentPath} from "data/util";
import {useHistory, useParams} from "react-router";

const MobileOverlay = ({content}) => {
    const {activeChannel} = useSelector(state => state.channel);
    const history = useHistory();
    const {contentId} = useParams();
    const updateActiveContent = (content) => {
        if (contentId !== _toString(content.id)) {
            history.push({
                pathname: buildContentPath(activeChannel, content),
                search: location.search
            });
        }
    };
    return (
        <div className="has-cursor-pointer" onClick={(e) => updateActiveContent(content)}>
            <div className="card has-min-width-200 has-max-width-200 has-border-radius-6 white-space-normal has-cursor-default has-text-centered" >
                <div className="card-content has-padding-5">
                    {content.store.name}
                </div>
            </div>
            <img className="marker-arrow" src={MarkerArrow}/>
        </div>
    )
};

export default MobileOverlay
