import React, {useState} from 'react';
import {useDispatch, useSelector} from "react-redux";
import {setActiveContent} from "../../../redux/action/content";
import {faStar} from "@fortawesome/free-solid-svg-icons";
import {faStar as fasStar} from "@fortawesome/free-regular-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {useHistory, useParams} from "react-router";
import {toString as _toString} from "lodash";
import {buildContentPath} from "../../../redux/reducers/util";

const Store = ({content}) => {
    const [favorite, setFavorite] = useState(false);
    const {activeChannel} = useSelector(state => state.channel);
    const history = useHistory();
    const {contentId} = useParams();
    const updateActiveContent = (content) => {
        if (contentId !== _toString(content.id)) {
            history.push(buildContentPath(activeChannel, content))
        }
    };
    return (
        <>
            <div className="video media has-padding-bottom-20 is-marginless ">
                <figure className="media-left">
                    <p className="image is-120x68">
                        <img className="image thumbnail" src={content.thumbnailUrl} />
                    </p>
                </figure>
                <div className="media-content">
                    <div className="content">
                        <p className="is-marginless">
                            <FontAwesomeIcon
                                icon={favorite ? faStar : fasStar}
                                className="has-margin-right-5 has-cursor-pointer"
                                color={favorite ? "#e8cd03" : ""}
                                size={'1x'}
                                onClick={(e) => setFavorite(!favorite)} />
                            <strong className="has-cursor-pointer"  onClick={() => updateActiveContent(content)}>{content.store.name}</strong>
                        </p>
                        <p className="has-text-grey-light is-marginless"  onClick={() => updateActiveContent(content)}>
                            <small className="has-text-weight-semibold">{content.store.address1}</small>
                        </p>
                    </div>
                </div>
            </div>
        </>
    )
};
export default Store;
