import React from 'react';
import {useDispatch, useSelector} from "react-redux";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faChevronLeft} from "@fortawesome/free-solid-svg-icons";
import YouTube from "react-youtube";
import CommentList from "../../common/comment/CommentList";
import {useHistory} from "react-router";
import {buildContentPath} from "../../../redux/reducers/util";

const ContentDetail = () => {
    const {activeContent} = useSelector(state => state.content);
    const {activeChannel} = useSelector(state => state.channel);
    const dispatch = useDispatch();
    const history = useHistory();
    const resetActiveContent = (e) => {
        history.push(buildContentPath(activeChannel, null))
    };

    return (
        <div className="columns flex-column is-paddingless box is-fullheight-with-navbar-fixed-height">
            <div className="column is-full is-marginless is-bottom-borderless text-overflow-ellipsis" style={{'lineHeight': '2'}}>
                <FontAwesomeIcon className="back-content-detail is-pulled-left has-margin-right-15" icon={faChevronLeft} size={'2x'} onClick={resetActiveContent}/>
                <span><strong>{activeContent.title}</strong></span>
                {/*<FontAwesomeIcon className="back-content-detail is-pulled-left" icon={faLongArrowAltLeft} size={'2x'} onClick={resetActiveContent}/>*/}
            </div>
            <YouTube
                videoId={activeContent.videoId}                  // defaults -> null
                id={activeContent.videoId}                       // defaults -> null
                className="youtube-container"                // defaults -> null
                containerClassName="column is-full is-marginless is-paddingless is-top-borderless"       // defaults -> ''
                // opts={opts}                        // defaults -> {}
                // onReady={func}                    // defaults -> noop
                // onPlay={func}                     // defaults -> noop
                // onPause={func}                    // defaults -> noop
                // onEnd={func}                      // defaults -> noop
                // onError={func}                    // defaults -> noop
                // onStateChange={func}              // defaults -> noop
                // onPlaybackRateChange={func}       // defaults -> noop
                // onPlaybackQualityChange={func}    // defaults -> noop
            />
            <CommentList />
        </div>
    )
};

export default ContentDetail;
